    function renderdaysofthemonth() {
        let days = []
        let num = 0
        for (let i = 0; i < 6; i++) {
            let week = []
            if (i === 0) {
                for (let j = 0; j < 7; j++) {
                    if (j < firstOfMonthINdex) {
                        week.push("")
                    } else {
                        num++

                        week.push(num)
                    }
                }
            } else {
                for (let j = 0; j < 7; j++) {
                    if (num >= endOfMonth) {
                        week.push("")
                    } else {
                        num++
                        week.push(num)
                    }
                }
            }
            days.push(week)
        }
        function changevalue(value, index) {
            if (value === "") {
                return <td
                    key={index}
                    style={{
                        backgroundColor: "#494b66"
                    }}
                >
                    {value}</td>
            } else {
                if (value === moment().get('date')) {
                    return <td
                        style={{
                            backgroundColor: "#075de8",
                            color: "white",
                            cursor: "pointer",
                            border: "1px solid black",
                        }}
                        key={index}
                        onClick={() => {
                            onDayClick(value)
                        }}>{value}</td>
                } else {
                    return <td
                        style={{
                            cursor: "pointer",
                            border: "1px solid black",
                        }}
                        key={index}
                        onClick={() => {
                            onDayClick(value)
                        }}>{value}</td>
                }
            }
        }
        var finalDays = days.map(subarray => subarray.map(changevalue))
        return finalDays

    }

