/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unreachable */
import React, { useState, useEffect } from 'react'
import { useHistory } from "react-router-dom";
import Cookies from 'universal-cookie';
import "./Day.css"
import moment from "moment"

export default function Day() {
    const dayinfo = JSON.stringify(localStorage.getItem('dayinfo'))
    const history = useHistory()
    const months = [
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July",
        "August",
        "September",
        "October",
        "November",
        "December"
    ]

    // const hours = ["0:0", "0:15", "0:30", "0:45", "1:0", "1:15", "1:30", "1:45", "2:0", "2:15", "2:30", "2:45", "3:0", "3:15", "3:30", "3:45", "4:0", "4:15", "4:30", "4:45", "5:0", "5:15", "5:30", "5:45", "6:0", "6:15", "6:30", "6:45", "7:0", "7:15", "7:30", "7:45", "8:0", "8:15", "8:30", "8:45", "9:0", "9:15", "9:30", "9:45", "10:0", "10:15", "10:30", "10:45", "11:0", "11:15", "11:30", "11:45", "12:0", "12:15", "12:30", "12:45", "13:0", "13:15", "13:30", "13:45", "14:0", "14:15", "14:30", "14:45", "15:0", "15:15", "15:30", "15:45", "16:0", "16:15", "16:30", "16:45", "17:0", "17:15", "17:30", "17:45", "18:0", "18:15", "18:30", "18:45", "19:0", "19:15", "19:30", "19:45", "20:0", "20:15", "20:30", "20:45", "21:0", "21:15", "21:30", "21:45", "22:0", "22:15", "22:30", "22:45", "23:0", "23:15", "23:30", "23:45"]

    const [dayDate, setdayDate] = useState([1])

    function close() {
        console.log(dayinfo)
        localStorage.clear()
        history.push("/Calendar")
    }

    function renderTable() {
        var hoursTable = []
        var hour = 0
        var index = 0
        for (let i = 0; i < 24; i++) {
            var min = 0
            for (let j = 0; j < 4; j++) {
                hoursTable.push(<tr key={index}><th>{`${hour}:${min}`}</th></tr>)
                index++
                min += 15
            }
            hour++
        }
        return <table><tbody>{hoursTable}</tbody></table>
    }


    function DayInformation() {
        if (dayDate.length <= 0) {
            return <>No Info</>
        } else {
            return renderTable()
        }
    }

    return (

        <div className="daybody">
            <div className="logbar" >
                <div className="logout"
                    onClick={
                        () => {
                            close()
                        }
                    }
                >
                    <h1>{`<--`}</h1>
                </div>
            </div>

            <div className="dayinfo">
                <h1>{`${months[localStorage.getItem('month')]} `}</h1>
                <h1>{` ${localStorage.getItem('day')}, `}</h1>
                <h1>{`${localStorage.getItem('year')}`}</h1>
            </div>


            <div className="dayinfoarea">
                {
                    DayInformation()
                }
            </div>

        </div>

    )
}
