                    return <td
                        style={{
                            backgroundColor: "#075de8",
                            color: "white",
                            cursor: "pointer",
                            border: "1px solid black",
                        }}
                        key={index}
                        onClick={() => {
                            onDayClick(value)
                        }}>{value}</td>
                } else {
                    return <td
                        style={{
                            cursor: "pointer",
                            border: "1px solid black",
                        }}
                        key={index}
                        onClick={() => {
                            onDayClick(value)
                        }}>{value}</td>
                }
            }
        }
        var finalDays = days.map(subarray => subarray.map(changevalue))
